  static Map<String, int> slugs = challenges.asMap().map(
      (index, challengeData) => MapEntry(originalSlug(challengeData), index));

  static String originalSlug(NamedChallengeData challengeData) {
    return '${challengeData.type.toSlug()}-${challengeData.slug}';
  }

  const OriginalChallenges({Key? key}) : super(key: key);

  @override
  _OriginalChallengesState createState() => _OriginalChallengesState();
}

class _OriginalChallengesState extends State<OriginalChallenges> {
  bool _showCompleted = true;

  void _openChallenge(String slug) {
    Router.of(context)
        .routerDelegate
        .setNewRoutePath(NyaNyaRoutePath.originalChallenge(slug));
  }

  @override
  Widget build(BuildContext context) {
    List<int> challengeIndices =
        Iterable<int>.generate(OriginalChallenges.challenges.length)
            .toList(growable: false);

    final progression = context.watch<ChallengeProgressionManager>();
    final times = progression.getTimes();
    final cleared = times.keys.toSet();

    if (!_showCompleted) {
      challengeIndices = SplayTreeSet<int>.from(challengeIndices)
          .difference(cleared)
          .toList(growable: false);
    }

    return Column(
      children: [
        Expanded(
          child: BoardList(
              itemCount: challengeIndices.length,
              tileBuilder: (BuildContext context, int index) {
                return _buildChallengeTile(
                    OriginalChallenges.challenges[challengeIndices[index]],
                    times[index]);
              },
              cardBuilder: (BuildContext context, int index) =>
                  _buildChallengeCard(
                      OriginalChallenges.challenges[challengeIndices[index]],
                      times[index])),
        ),
        CompletionIndicator(
          completedRatio: cleared.length / OriginalChallenges.challenges.length,
          showCompleted: _showCompleted,
          onChanged: (bool? value) {
            if (value != null) {
              setState(() {
                _showCompleted = value;
              });
            }
          },
        )
      ],
    );
  }

  Widget _buildChallengeTile(
      NamedChallengeData namedChallengeData, Duration? time) {
    final bool cleared = time != null;

    return ListTile(
      title: Text(_fullLocalizedName(namedChallengeData)),
      subtitle: Text(namedChallengeData.data.type.toLocalizedString(context)),
      trailing: cleared
          ? Row(mainAxisSize: MainAxisSize.min, children: [
              Text(_formatTime(time)),
              const Icon(
                Icons.check,
                color: Colors.green,
              )
            ])
          : null,
      onTap: () {
        _openChallenge(OriginalChallenges.originalSlug(namedChallengeData));
      },
    );
  }

  Widget _buildChallengeCard(
      NamedChallengeData namedChallengeData, Duration? time) {
    final bool cleared = time != null;

    return InkWell(
      key: ValueKey(namedChallengeData.slug),
      child: BoardCard(
        game: namedChallengeData.data.getGame(),
        description: [
          Text(
            _fullLocalizedName(namedChallengeData),
            style: const TextStyle(fontWeight: FontWeight.bold),
          ),
          Text(cleared ? _formatTime(time) : '')
        ],
        cleared: cleared,
      ),
      onTap: () {
        _openChallenge(OriginalChallenges.originalSlug(namedChallengeData));
      },
    );
  }

  String _fullLocalizedName(NamedChallengeData namedChallengeData) {
    return namedChallengeData.data.type.toLocalizedString(context) +
        namedChallengeData.name;
  }

  String _formatTime(Duration time) {
    final String seconds = time.inSeconds.toString();
    final String decimals =
        (time.inMilliseconds % 1000 ~/ 10).toString().padLeft(2, '0');
    return '$seconds.${decimals}s';
  }
}
